import React, { useState, useEffect } from "react";
import axios from "axios";
import swal from 'sweetalert';
import * as toastr from 'toastr';
import 'toastr/build/toastr.css';

function Register (){

    const handleInput = event => {
      console.log({"zip":event.target.value, "message":"handleinput"})
      let pattern = /^[0-9]{5}/;
      let result = pattern.test(event.target.value)
      console.log({"":event.target.value, "message":"handleinput"})
      if(event.target.value.length == 5 && result && event.target.value!="00000"){
        let respuesta = getCP(event.target.value)
        console.log(respuesta)
           }
    };

    const getCP = async (zipcode) =>{
        try {
          console.log({"zip":zipcode, "message":"handleinput"})
            let formData = new FormData();
            formData.append("cp", zipcode);
            const url = "http://api.masksoftco.mx/direcciones/codigo-postal";

            let result = await axios({
                url,
                method: 'POST',
                dataType: 'json',
                ContentType: 'application/json',
                data: formData
            });
            if(result.data){
                let objectRes = result.data;
                return objectRes
            }else{
                swal("Opps!", "CÃ³digo postal incorrecto", "error");
            }


            console.log(result)
            console.log(result.data)



           // return (res.data ? res.data : false);

        } catch (error) {
            console.log(error);
        }
    }
    return(
        <div>
          <form className="demoForm">
            <h2>Sign up</h2>
            <div className="form-group">
              <label htmlFor="cp">CÃ³digo Postal: </label>
              <input type="text" className="form-control" name="cp" id="cp" onBlur={handleInput}/>
            </div>
            <div className="form-group">
              <label htmlFor="state">Estado: </label>
              <select className="form-control" name="state" id="state">
                <option value="volvo">Volvo</option>
                <option value="saab">Saab</option>
                <option value="mercedes">Mercedes</option>
                <option value="audi">Audi</option>
              </select>
            </div>
            <div className="form-group">
              <label htmlFor="alcaldia">Municipio/Alcaldia: </label>
              <select className="form-control" name="alcaldia" id="alcaldia">
                <option value="volvo">Volvo</option>
                <option value="saab">Saab</option>
                <option value="mercedes">Mercedes</option>
                <option value="audi">Audi</option>
              </select>
            </div>
            <div className="form-group">
              <label htmlFor="colonia">Municipio/Alcaldia: </label>
              <select className="form-control" name="colonia" id="colonia">
                <option value="volvo">Volvo</option>
                <option value="saab">Saab</option>
                <option value="mercedes">Mercedes</option>
                <option value="audi">Audi</option>
              </select>
            </div>
            <button type="submit" className="btn btn-primary">
                Sign up
            </button>
          </form>
        </div>
    );

}
export default Register;
