import React, { Component } from 'react';

class Countries extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      countries: [],
      colours: {}
    };
  }



componentDidMount() {
	this.setState({
		colours: {
			"#ff0000": "Red",
			"#00ff00": "Green",
			"#0000ff": "Blue"
		}
	});
}






render() {
	const colours= {
    "#ff0000": "Red",
    "#00ff00": "Green",
    "#0000ff": "Blue"
  }

	let coloursList = Object.keys(colours).map((k) => {
		return (
			<option key={k} value={k}>{colours[k]}</option>
		)
	}, this);

	return (
		<div>
			<select>
				{coloursList}
			</select>
		</div>
	);
}


}

export default Countries;
